parameters.functionCode:
// Enhanced data processing with validation
function processOrder(order) {
  try {
    return {
      shopify_order_id: order.id,
      order_number: order.order_number?.toString() || '',
      order_name: order.name || '',
      customer_email: order.email || '',
      customer_first_name: order.customer?.first_name || '',
      customer_last_name: order.customer?.last_name || '',
      total_price: parseFloat(order.total_price) || 0,
      subtotal_price: parseFloat(order.subtotal_price) || 0,
      total_tax: parseFloat(order.total_tax) || 0,
      currency: order.currency || 'USD',
      financial_status: order.financial_status || '',
      fulfillment_status: order.fulfillment_status || '',
      created_at: order.created_at,
      updated_at: order.updated_at,
      processed_at: order.processed_at,
      fulfilled_at: order.fulfilled_at,
      cancelled_at: order.cancelled_at,
      shipping_address: order.shipping_address || {},
      tags: order.tags ? order.tags.split(',').map(t => t.trim()).filter(t => t) : [],
      note: order.note || '',
      raw_shopify_data: order
    };
  } catch (error) {
    console.error('Error processing order:', error);
    return null;
  }
}

function processLineItem(orderId, line) {
  try {
    return {
      shopify_order_id: orderId,
      shopify_line_item_id: line.id,
      product_id: line.product_id,
      variant_id: line.variant_id,
      title: line.title || line.name || '',
      variant_title: line.variant_title || '',
      sku: line.sku || '',
      quantity: parseInt(line.quantity) || 0,
      price: parseFloat(line.price) || 0,
      fulfillment_status: line.fulfillment_status || '',
      fulfillable_quantity: parseInt(line.fulfillable_quantity) || 0,
      raw_line_item_data: line
    };
  } catch (error) {
    console.error('Error processing line item:', error);
    return null;
  }
}

const orders = [];
const orderLines = [];
const errors = [];

// Get all input items
const items = $input.all();

for (const item of items) {
  const responseData = item.json;
  // Handle Shopify API response format: {"orders": [...]}
  const ordersList = responseData.orders || [responseData];

  console.log(`Processing ${ordersList.length} orders from response`);

  for (const order of ordersList) {
    const processedOrder = processOrder(order);
    if (processedOrder) {
      orders.push(processedOrder);

      if (order.line_items && Array.isArray(order.line_items)) {
        order.line_items.forEach(line => {
          const processedLine = processLineItem(order.id, line);
          if (processedLine) {
            orderLines.push(processedLine);
          } else {
            errors.push(`Failed to process line item ${line.id} for order ${order.id}`);
          }
        });
      }
    } else {
      errors.push(`Failed to process order ${order.id}`);
    }
  }
}

// Add detailed logging
console.log('=== ORDER PROCESSING LOG ===');
console.log('Successfully processed orders:', orders.length);
console.log('Total order lines:', orderLines.length);
console.log('Processing errors:', errors.length);
if (orders.length > 0) {
  console.log('Sample order:', {
    id: orders[0].shopify_order_id,
    number: orders[0].order_number,
    total: orders[0].total_price
  });
}
if (errors.length > 0) {
  console.log('Errors:', errors);
}
console.log('========================');

const result = {
  orders,
  orderLines,
  orderCount: orders.length,
  lineItemCount: orderLines.length,
  errorCount: errors.length,
  errors: errors
};

return [{ json: result }];

